mapper.select:
SELECT COUNT(*) AS CNT
  FROM AC_DT A
 WHERE ACT_SEQ = #{actSeq}

SELECT COUNT(*) AS CNT
  FROM AC_DT
 WHERE ACT_SEQ = #{actSeq}
   AND TRD_CD = #{trdCd}
   AND TRD_DT = DATE_FORMAT(#{trdDt}, '%Y-%m-%d %H:%i')
   AND AMT = #{amt}

SELECT SUM(AMT) AS IN_OUT_SUM_AMT
  FROM AC_DT A
 WHERE ACT_SEQ = #{actSeq}
   AND TRD_CD IN (

	#{item}

   )

mapper.insert:

INSERT INTO AC_DT (
	   TRD_SEQ
	 , ACT_SEQ
	 , REL_TRD_SEQ
	 , TRD_CD
	 , AMT
	 , ITM_CD
	 , QTY
	 , PRC
	 , FEE
	 , TAX
	 , NOTE
	 , TLGRM_MSG_ID
	 , TRD_DT
	 , ED_DT
	 , FILE_SEQ
	 ) VALUES (
	   #{trdSeq}
	 , #{actSeq}
	 , #{relTrdSeq}
	 , #{trdCd}
	 , #{amt}
	 , #{itmCd}
	 , #{qty}
	 , #{prc}
	 , NULLIF(#{fee}, '0')
	 , NULLIF(#{tax}, '0')
	 , #{note}
	 , #{tlgrmMsgId}
	 , STR_TO_DATE(#{trdDt}, '%Y-%m-%dT%H:%i')
	 , STR_TO_DATE(#{edDt}, '%Y-%m-%d')
	 , #{fileSeq}
	 )

mapper.update:
UPDATE AC_DT
   SET ACT_SEQ = #{actSeq}
 WHERE TLGRM_MSG_ID = #{tlgrmMsgId}
