mapper.select:
SELECT COUNT(*) AS CNT
  FROM AC_DT A
 WHERE ACT_SEQ = #{actSeq}

SELECT SUM(AMT) AS IN_OUT_SUM_AMT
  FROM AC_DT A
 WHERE ACT_SEQ = #{actSeq}
   AND TRD_CD IN (

	#{item}

   )

mapper.insert:

INSERT INTO AC_DT (
	   TRD_SEQ
	 , ACT_SEQ
	 , REL_TRD_SEQ
	 , TRD_CD
	 , AMT
	 , ITM_CD
	 , QTY
	 , NOTE
	 , TLGRM_MSG_ID
	 , TRD_DT
	 , ED_DT
	 ) VALUES (
	   #{trdSeq}
	 , #{actSeq}
	 , NULLIF(#{relTrdSeq}, '')
	 , #{trdCd}
	 , #{amt}
	 , #{itmCd}
	 , NULLIF(#{qty}, '')
	 , #{note}
	 , #{tlgrmMsgId}
	 , STR_TO_DATE(#{trdDt}, '%Y-%m-%dT%H:%i')
	 , STR_TO_DATE(#{edDt}, '%Y-%m-%d')
	 )

mapper.update:
UPDATE AC_DT
   SET ACT_SEQ = #{actSeq}
 WHERE TLGRM_MSG_ID = #{tlgrmMsgId}
