mapper.select:
SELECT COUNT(*) AS CNT
  FROM AC_DT A
 WHERE ACT_SEQ = #{actSeq}

mapper.insert:

INSERT INTO AC_DT (
	   TRD_SEQ
	 , ACT_SEQ
	 , REL_TRD_SEQ
	 , TRD_CD
	 , AMT
	 , ITM_CD
	 , QTY
	 , NOTE
	 , TRD_DT
	 , ED_DT
	 ) VALUES (
	   #{trdSeq}
	 , #{actSeq}
	 , #{relTrdSeq}
	 , #{trdCd}
	 , #{amt}
	 , #{itmCd}
	 , #{qty}
	 , #{note}
	 , STR_TO_DATE(#{trdDt}, '%Y-%m-%dT%H:%i')
	 , STR_TO_DATE(#{edDt}, '%Y-%m-%d')
	 )
