mapper.select:
SELECT SEQ , BATCH_CD , (SELECT CD_NM FROM BC_CD_DT X WHERE X.CD_COL = 'BATCH_CD' AND X.CD = A.BATCH_CD) AS BATCH_NM , PARM_1ST , PARM_2ND , PARM_3RD , PARM_4TH , EXE_YN , SUC_YN , DATE_FORMAT(EXE_ST_DT, "%Y-%m-%d %H:%i:%s") AS EXE_ST_DT , DATE_FORMAT(EXE_ED_DT, "%Y-%m-%d %H:%i:%s") AS EXE_ED_DT FROM BC_BATCH_MT A WHERE 1 = 1
AND BATCH_CD = #{batchCd}
AND EXE_YN = #{exeYn}
ORDER BY SEQ DESC
ORDER BY SEQ ASC
LIMIT #{limit}
OFFSET #{offset}

SELECT COUNT(*) AS CNT
  FROM BC_BATCH_MT A
 WHERE 1 = 1

   AND BATCH_CD = #{batchCd}


   AND EXE_YN = #{exeYn}

mapper.insert:

INSERT INTO BC_BATCH_MT (
	   SEQ
	 , BATCH_CD
	 , PARM_1ST
	 , PARM_2ND
	 , PARM_3RD
	 , PARM_4TH
	 , EXE_YN
	 , SUC_YN
	 ) VALUES (
	   #{seq}
	 , #{batchCd}
	 , #{parm1st}
	 , #{parm2nd}
	 , #{parm3rd}
	 , #{parm4th}
	 , #{exeYn}
	 , #{sucYn}
	 )

mapper.update:
UPDATE BC_BATCH_MT
   SET SEQ = #{seq}

     , EXE_YN = #{exeYn}



     , EXE_ST_DT = NOW()


     , EXE_ED_DT = NOW()




     , SUC_YN = #{sucYn}

 WHERE SEQ = #{seq}
