mapper.select:
SELECT SEQ , BATCH_CD , PARM_1ST , PARM_2ND , PARM_3RD , PARM_4TH , EXE_YN , SUC_YN FROM BC_BATCH_MT WHERE 1 = 1
AND BATCH_CD = #{batchCd}
AND EXE_YN = #{exeYn}
ORDER BY SEQ ASC
LIMIT #{srchCnt}

mapper.update:
UPDATE BC_BATCH_MT
   SET SEQ = #{seq}

     , EXE_YN = #{exeYn}
     , EXE_DT = NOW()


     , SUC_YN = #{sucYn}

 WHERE SEQ = #{seq}
