mapper.select:
SELECT CD_COL , CD_COL_NM , USE_YN FROM BC_CD_MT WHERE 1 = 1
AND USE_YN = #{useYn}
ORDER BY CD_COL ASC

mapper.insert:

INSERT INTO BC_CD_MT (
	   CD_COL
	 , CD_COL_NM
	 , USE_YN
	 ) VALUES (
	   #{cdCol}
	 , #{cdColNm}
	 , #{useYn}
	 )

mapper.update:
UPDATE BC_CD_MT
   SET CD_COL = #{cdCol}
     , CD_COL_NM = #{cdColNm}
     , USE_YN = #{useYn}
 WHERE CD_COL = #{cdCol}
