mapper.select:
SELECT CD_COL , CD , CD_NM , ORD , USE_YN FROM BC_CD_DT WHERE 1 = 1
AND CD_COL = #{cdCol}
AND CD = #{cd}
AND CD_NM = #{cdNm}
AND USE_YN = 'Y' ORDER BY ORD ASC

SELECT COUNT(*) AS CNT
  FROM BC_CD_DT
 WHERE 1 = 1

   AND CD_COL = #{cdCol}


   AND CD = #{cd}


   AND CD_NM = #{cdNm}


   AND USE_YN = #{useYn}

mapper.insert:

INSERT INTO BC_CD_DT (
	   CD_COL
	 , CD
	 , CD_NM
	 , ORD
	 , USE_YN
	 ) VALUES (
	   #{cdCol}
	 , (SELECT LPAD(CONVERT(NVL(MAX(CD), '00000'), INT) + 1, 5, '0') FROM BC_CD_DT X WHERE CD_COL = #{cdCol})
	 , #{cdNm}
	 , (SELECT NVL(MAX(ORD), 0) + 1 FROM BC_CD_DT X WHERE CD_COL = #{cdCol})
	 , 'Y'
	 )

mapper.update:
UPDATE BC_CD_DT
   SET CD = #{cd}

     , CD_NM = #{cdNm}


     , ORD = #{ord}


     , USE_YN = ${useYn}

 WHERE CD_COL = #{cdCol}
   AND CD = #{cd}

UPDATE BC_CD_DT
   SET USE_YN = 'N'
 WHERE CD_COL = #{cdCol}
   AND CD = #{cd}
