mapper.select:
SELECT COUNT(*) AS CNT
  FROM BC_CD_DT
 WHERE CD_COL = #{cdCol}
   AND CD_NM = #{cdNm}

mapper.insert:

INSERT INTO BC_CD_DT (
	   CD_COL
	 , CD
	 , CD_NM
	 , ORD
	 , USE_YN
	 ) VALUES (
	   #{cdCol}
	 , (SELECT LPAD(CONVERT(NVL(MAX(CD), '00000'), INT) + 1, 5, '0') FROM BC_CD_DT X WHERE CD_COL = #{cdCol})
	 , #{cdNm}
	 , (SELECT NVL(MAX(ORD), 0) + 1 FROM BC_CD_DT X WHERE CD_COL = #{cdCol})
	 , 'Y'
	 )
