mapper.select:

SELECT COUNT(*) AS CNT
  FROM DT_ITM_MT
 WHERE DART_ITM_CD = #{dartItmCd}

mapper.insert:

INSERT INTO DT_ITM_MT (
	   DART_ITM_CD
	 , DART_ITM_CD_NM
	 , ITM_CD
	 , MOD_DT
	 ) VALUES (
	   #{dartItmCd}
	 , #{dartItmCdNm}
	 , #{itmCd}
	 , STR_TO_DATE(#{modDt}, '%Y%m%d')
	 )

mapper.update:

UPDATE DT_ITM_MT
   SET DART_ITM_CD = #{dartItmCd}


     , DART_ITM_CD_NM = #{dartItmCdNm}


     , ITM_CD = #{itmCd}


     , MOD_DT = STR_TO_DATE(#{modDt}, '%Y%m%d')


 WHERE DART_ITM_CD = #{dartItmCd}
