mapper.select:

SELECT COUNT(*) AS CNT
  FROM KX_ITM_MT
 WHERE ITM_CD = #{itmCd}

SELECT ITM_CD , ITM_NM , MKT_CD , (SELECT CD_NM FROM BC_CD_DT X WHERE X.CD_COL = 'MKT_CD' AND A.MKT_CD = X.CD) AS MKT_NM , SPAC_YN , ITM_KND_CD , (SELECT CD_NM FROM BC_CD_DT X WHERE X.CD_COL = 'ITM_CL_CD' AND A.ITM_KND_CD = X.CD) AS ITM_KND_NM , ITM_CL_CD , (SELECT CD_NM FROM BC_CD_DT X WHERE X.CD_COL = 'ITM_CL_CD' AND A.ITM_CL_CD = X.CD) AS ITM_CL_NM FROM KX_ITM_MT A WHERE 1 = 1
AND MKT_CD = #{mktCd}
AND ITM_NM LIKE CONCAT('%', #{itmNm}, '%')
AND SPAC_YN = #{spacYn}
ORDER BY ITM_NM ASC

mapper.insert:

INSERT INTO KX_ITM_MT (
	   ITM_CD
	 , ITM_NM
	 , MKT_CD
	 , STD_ITM_CD
	 , PUB_DT
	 , SPAC_YN
	 , ITM_KND_CD
	 , ITM_CL_CD
	 ) VALUES (
	   #{itmCd}
	 , #{itmNm}
	 , #{mktCd}
	 , #{stdItmCd}
	 , STR_TO_DATE(#{pubDt}, '%Y/%m/%d')
	 , #{spacYn}
	 , #{itmKndCd}
	 , #{itmClCd}
	 )

mapper.update:

UPDATE KX_ITM_MT
   SET ITM_CD = #{itmCd}


     , SPAC_YN = #{spacYn}


     , ITM_KND_CD = #{itmKndCd}


     , ITM_CL_CD = #{itmClCd}


 WHERE ITM_CD = #{itmCd}
